const targetWeight = items[0].json.body.targetWeight;
const exerciseType = items[0].json.body.exerciseType || 'COMPOUND';

const sets = [];

if (targetWeight < 20) {
  sets.push({ weight: 'Bodyweight', reps: 10, note: 'Dynamic Stretch' });
} else {
  // Empty Bar
  sets.push({ weight: 20, reps: 10, note: 'Focus on technique' });

  if (targetWeight > 40) {
    sets.push({ weight: Math.round(targetWeight * 0.4 / 2.5) * 2.5, reps: 5, note: 'Slow Tempo' });
  }
  if (targetWeight > 60) {
    sets.push({ weight: Math.round(targetWeight * 0.6 / 2.5) * 2.5, reps: 3, note: 'Explosive' });
  }
  if (targetWeight > 80) {
    sets.push({ weight: Math.round(targetWeight * 0.8 / 2.5) * 2.5, reps: 1, note: 'Primer Rep' });
  }
}

return [{ json: { targetWeight, warmups: sets } }];
