const { exerciseType, rpe, setNumber } = items[0].json.body;
let seconds = 90;

if (exerciseType === 'COMPOUND' || exerciseType === 'SQUAT' || exerciseType === 'DEADLIFT') {
  seconds = 180;
  if (rpe > 9) seconds += 60; // Extra rest for heavy sets
}

if (exerciseType === 'ISOLATION') {
  seconds = 60;
  if (setNumber === 1) seconds = 45;
}

return [{ json: { recommendedRest: seconds, type: exerciseType } }];
