const logs = items[0].json.body.logs; // Array of sets
const muscleGroups = {};

// 1. Sum Tonnage per Muscle
for (const set of logs) {
  const muscle = set.muscleTarget || 'General';
  if (!muscleGroups[muscle]) muscleGroups[muscle] = 0;
  muscleGroups[muscle] += (set.weight * set.reps);
}

// 2. Check MRV (Max Recoverable Volume)
const warnings = [];
const limits = { 'Chest': 8000, 'Legs': 12000, 'Back': 10000 };

for (const [m, vol] of Object.entries(muscleGroups)) {
  if (limits[m] && vol > limits[m]) {
    warnings.push(`Overreaching detected: ${m} volume (${vol}kg) exceeds cap.`);
  }
}

return [{ json: { muscleGroups, warnings } }];
