// ACWR Calculation (Acute:Chronic Workload Ratio)
const history = items.map(i => i.json);

// Last 7 days load
const acuteLoad = history.slice(-7).reduce((acc, curr) => acc + parseInt(curr.daily_load), 0) / 7;

// Last 28 days load
const chronicLoad = history.reduce((acc, curr) => acc + parseInt(curr.daily_load), 0) / 28;

const ratio = acuteLoad / (chronicLoad || 1);
let status = 'OPTIMAL';

if (ratio > 1.5) status = 'DANGER_ZONE';
else if (ratio > 1.3) status = 'HIGH_RISK';
else if (ratio < 0.8) status = 'DETRAINING';

return [{ json: { acuteLoad, chronicLoad, ratio, status } }];
