const { errorCode, context, user } = items[0].json.body;

// 1. Severity Classification
let severity = 'LOW';
if (errorCode.startsWith('DB_')) severity = 'HIGH';
if (errorCode === 'PAYMENT_FAIL') severity = 'CRITICAL';

// 2. Alert Routing
let channel = 'dev-logs';
if (severity === 'CRITICAL') channel = 'urgent-alerts';

return [{ json: { severity, channel, alertMessage: `Error ${errorCode} for ${user}: ${context}` } }];
